import React from "react";
import { useNavigate } from "react-router-dom";
import * as S from "./styled";
import arrowIcon from "../../../assets/arrow.svg";

const MyPage = () => {
  const navigate = useNavigate();

  const menuItems = [
    {
      id: 1,
      title: "내 게시글",
      path: "/my-page/my-posts",
    },
    {
      id: 2,
      title: "스크랩한 게시글",
      path: "/my-page/scrapped",
    },
    {
      id: 3,
      title: "알림 설정",
      path: "/my-page/notifications",
    },
    {
      id: 4,
      title: "로그아웃",
      onClick: () => {
        localStorage.removeItem("token");
        navigate("/login");
      },
    },
  ];

  const handleMenuClick = (item) => {
    if (item.onClick) {
      item.onClick();
    } else {
      navigate(item.path);
    }
  };

  return (
    <S.Container>
      <S.Header>
        <S.Title>마이페이지</S.Title>
      </S.Header>

      <S.ProfileSection>
        <S.ProfileImage />
        <S.ProfileInfo>
          <S.UserName>사용자 이름</S.UserName>
          <S.UserEmail>user@example.com</S.UserEmail>
        </S.ProfileInfo>
      </S.ProfileSection>

      <S.MenuList>
        {menuItems.map((item) => (
          <S.MenuItem key={item.id} onClick={() => handleMenuClick(item)}>
            <S.MenuTitle>{item.title}</S.MenuTitle>
            <S.MenuArrow src={arrowIcon} alt="이동" />
          </S.MenuItem>
        ))}
      </S.MenuList>
    </S.Container>
  );
};

export default MyPage;
