import styled from "styled-components";

export const Container = styled.div`
  width: 100%;
  min-height: 100vh;
  background: #fff;
`;

export const ChatListWrapper = styled.div`
  display: flex;
  flex-direction: column;
  width: 100%;
`;

export const ChatItem = styled.div`
  display: flex;
  flex-direction: row;
  align-items: flex-start;
  padding: 16px 16px 12px 16px;
  border-bottom: 1px solid #f0f0f0;
  cursor: pointer;
  background: #fff;
  transition: background 0.2s;
  &:hover {
    background: #f7faff;
  }
`;

export const ProfileCircle = styled.div`
  width: 40px;
  height: 40px;
  border-radius: 50%;
  background: #bddde4;
  color: #fff;
  font-size: 20px;
  font-weight: bold;
  display: flex;
  align-items: center;
  justify-content: center;
  margin-right: 14px;
`;

export const ChatInfo = styled.div`
  flex: 1;
  display: flex;
  flex-direction: column;
`;

export const ChatTitle = styled.div`
  font-size: 16px;
  font-weight: 600;
  color: #222;
`;

export const ChatMeta = styled.div`
  display: flex;
  align-items: center;
  gap: 8px;
  font-size: 12px;
  color: #aaa;
  margin-bottom: 2px;
`;

export const ChatPreview = styled.div`
  font-size: 14px;
  color: #444;
  margin-top: 2px;
  white-space: nowrap;
  overflow: hidden;
  text-overflow: ellipsis;
`;

export const UnreadBadge = styled.div`
  background: #222;
  color: #fff;
  border-radius: 50%;
  min-width: 18px;
  height: 18px;
  font-size: 12px;
  display: flex;
  align-items: center;
  justify-content: center;
  padding: 0 5px;
  margin-left: 4px;
`;
