import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import * as S from "./styled";
import PostCard from "../../components/Post/PostCard/PostCard";
import Header from "../../components/common/Header/Header";
import Navbar from "../../components/common/Navbar/Navbar";

const PostList = () => {
  const navigate = useNavigate();
  const [selectedCategories, setSelectedCategories] = useState([]);
  const [selectedStatus, setSelectedStatus] = useState("all");

  const categories = ["냉장고", "에어컨", "세탁기"];
  const statusOptions = [
    { value: "all", label: "전체 상태" },
    { value: "available", label: "판매중" },
    { value: "sold", label: "판매완료" },
  ];

  const toggleCategory = (category) => {
    setSelectedCategories((prev) =>
      prev.includes(category)
        ? prev.filter((c) => c !== category)
        : [...prev, category]
    );
  };

  const [posts, setPosts] = useState([
    {
      id: 1,
      title: "삼성 냉장고 팝니다",
      price: 500000,
      imageUrl: "https://via.placeholder.com/300x200",
      isScraped: false,
      category: "냉장고",
      status: "available",
    },
    {
      id: 2,
      title: "LG 통돌이 세탁기 급처",
      price: 150000,
      imageUrl: "https://via.placeholder.com/300x200",
      isScraped: true,
      category: "세탁기",
      status: "available",
    },
    {
      id: 3,
      title: "스탠드 에어컨 판매합니다",
      price: 350000,
      imageUrl: "https://via.placeholder.com/300x200",
      isScraped: false,
      category: "에어컨",
      status: "available",
    },
    {
      id: 4,
      title: "건조기 팔아요 (2년 사용)",
      price: 400000,
      imageUrl: "https://via.placeholder.com/300x200",
      isScraped: true,
      category: "세탁기",
      status: "sold",
    },
    {
      id: 5,
      title: "삼성 비스포크 냉장고",
      price: 1200000,
      imageUrl: "https://via.placeholder.com/300x200",
      isScraped: false,
      category: "냉장고",
      status: "available",
    },
    {
      id: 6,
      title: "벽걸이 에어컨 판매",
      price: 280000,
      imageUrl: "https://via.placeholder.com/300x200",
      isScraped: false,
      category: "에어컨",
      status: "available",
    },
    {
      id: 7,
      title: "LG 트롬 세탁기",
      price: 450000,
      imageUrl: "https://via.placeholder.com/300x200",
      isScraped: true,
      category: "세탁기",
      status: "available",
    },
    {
      id: 8,
      title: "삼성 냉장고 (새제품)",
      price: 650000,
      imageUrl: "https://via.placeholder.com/300x200",
      isScraped: false,
      category: "냉장고",
      status: "available",
    },
  ]);

  const handleScrap = (postId) => {
    setPosts((prevPosts) =>
      prevPosts.map((post) =>
        post.id === postId ? { ...post, isScraped: !post.isScraped } : post
      )
    );
  };

  const filteredPosts = posts.filter((post) => {
    const categoryMatch =
      selectedCategories.length === 0 ||
      selectedCategories.includes(post.category);
    const statusMatch =
      selectedStatus === "all" || post.status === selectedStatus;
    return categoryMatch && statusMatch;
  });

  return (
    <>
      <Header />
      <S.Container>
        <S.FilterSection>
          {categories.map((category) => (
            <S.FilterButton
              key={category}
              isActive={selectedCategories.includes(category)}
              onClick={() => toggleCategory(category)}
            >
              {category}
            </S.FilterButton>
          ))}
          <S.Divider />
          {statusOptions.map((option) => (
            <S.FilterButton
              key={option.value}
              isActive={selectedStatus === option.value}
              onClick={() => setSelectedStatus(option.value)}
            >
              {option.label}
            </S.FilterButton>
          ))}
        </S.FilterSection>

        <S.PostGrid>
          {filteredPosts.map((post) => (
            <PostCard
              key={post.id}
              {...post}
              onScrapClick={() => handleScrap(post.id)}
            />
          ))}
        </S.PostGrid>

        <S.AddButton onClick={() => navigate("/postlist/create")}>
          +
        </S.AddButton>
      </S.Container>
      <Navbar />
    </>
  );
};

export default PostList;
