import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import * as S from "./styled";
import cameraIcon from "../../assets/camera.svg";

const PostCreate = () => {
  const navigate = useNavigate();
  const [formData, setFormData] = useState({
    title: "",
    category: "",
    tradeType: "",
    images: [],
    modelName: "",
    specs: {
      brand: "",
      capacity: "",
      powerConsumption: "",
      size: "",
    },
    defectQuestions: {
      q1: "",
      q2: "",
      q3: "",
    },
    description: "",
    price: "",
    sellInfo: {
      quantity: "",
      location: "",
      pickupPrice: "",
    },
  });

  const categories = ["냉장고", "에어컨", "세탁기"];
  const tradeTypes = ["사기", "팔기", "마감"];

  const defectQuestions = {
    냉장고: [
      "냉동실 내부에 성에가 과도하게 생기거나 물이 새는 현상이 있었나요?",
      "냉장고 내부 온도가 설정한 대로 유지되지 않는 경우가 있었나요?",
      "냉장고 작동 중 소음이 지속적으로 들려 불편함을 느낀 적이 있나요?",
    ],
    에어컨: [
      "냉방 성능이 떨어지거나 실내 온도가 일정하게 유지되지 않는 문제가 있었나요?",
      "실외기에서 비정상적인 소음이나 진동이 발생한 적이 있나요?",
      "실내기에서 물이 새거나 냄새가 나는 현상이 있었나요?",
    ],
    세탁기: [
      "세탁 또는 탈수 중 비정상적인 소음이나 진동이 있었나요?",
      "물 배출이 잘 되지 않거나 누수가 있었나요?",
      "세탁이 완료된 후에도 옷에서 냄새가 나거나 세제가 남아있는 경우가 있었나요?",
    ],
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    if (name.includes(".")) {
      const [parent, child] = name.split(".");
      setFormData((prev) => ({
        ...prev,
        [parent]: {
          ...prev[parent],
          [child]: value,
        },
      }));
    } else {
      setFormData((prev) => ({
        ...prev,
        [name]: value,
      }));
    }
  };

  const handleDefectQuestionChange = (questionNumber, value) => {
    setFormData((prev) => ({
      ...prev,
      defectQuestions: {
        ...prev.defectQuestions,
        [`q${questionNumber}`]: value,
      },
    }));
  };

  const handleImageUpload = (e) => {
    // TODO: 이미지 업로드 처리
    console.log("Image upload:", e.target.files);
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    // TODO: 폼 제출 처리
    console.log("Form submitted:", formData);
    navigate("/post");
  };

  return (
    <S.Container>
      <S.Form onSubmit={handleSubmit}>
        <S.Section>
          <S.Label>제목</S.Label>
          <S.Input
            type="text"
            name="title"
            value={formData.title}
            onChange={handleChange}
            placeholder="브랜드, 제품 종류, 모델명을 포함하여 작성해주세요"
          />
        </S.Section>

        <S.Section>
          <S.Label>거래 유형</S.Label>
          <S.Select
            name="tradeType"
            value={formData.tradeType}
            onChange={handleChange}
          >
            <option value="">선택해주세요</option>
            {tradeTypes.map((type) => (
              <option key={type} value={type}>
                {type}
              </option>
            ))}
          </S.Select>
        </S.Section>

        <S.Section>
          <S.Label>제품 종류</S.Label>
          <S.Select
            name="category"
            value={formData.category}
            onChange={handleChange}
          >
            <option value="">선택해주세요</option>
            {categories.map((category) => (
              <option key={category} value={category}>
                {category}
              </option>
            ))}
          </S.Select>
        </S.Section>

        <S.Section>
          <S.Label>제품 사진</S.Label>
          <input
            type="file"
            accept="image/*"
            multiple
            onChange={handleImageUpload}
            style={{ display: "none" }}
            id="imageUpload"
          />
          <S.ImageUploadButton as="label" htmlFor="imageUpload">
            <img src={cameraIcon} alt="카메라" />
            <span>사진 추가하기 (카메라/갤러리)</span>
          </S.ImageUploadButton>
        </S.Section>

        <S.Section>
          <S.Label>제품 정보</S.Label>
          <S.Input
            type="text"
            name="modelName"
            value={formData.modelName}
            onChange={handleChange}
            placeholder="정확한 모델명을 입력해주세요"
          />
          <S.Input
            type="text"
            name="specs.brand"
            value={formData.specs.brand}
            onChange={handleChange}
            placeholder="브랜드"
          />
          <S.Input
            type="text"
            name="specs.capacity"
            value={formData.specs.capacity}
            onChange={handleChange}
            placeholder="용량 (예: 500L, 18kg)"
          />
          <S.Input
            type="text"
            name="specs.powerConsumption"
            value={formData.specs.powerConsumption}
            onChange={handleChange}
            placeholder="전력 소비량 (예: 1등급, 500W)"
          />
          <S.Input
            type="text"
            name="specs.size"
            value={formData.specs.size}
            onChange={handleChange}
            placeholder="크기 (가로 x 세로 x 높이)"
          />
        </S.Section>

        {formData.category && (
          <S.Section>
            <S.Label>하자 정보</S.Label>
            {defectQuestions[formData.category].map((question, index) => (
              <S.QuestionBox key={index}>
                <S.Question>{question}</S.Question>
                <S.RadioGroup>
                  <label>
                    <input
                      type="radio"
                      name={`defect_q${index + 1}`}
                      value="yes"
                      onChange={(e) =>
                        handleDefectQuestionChange(index + 1, e.target.value)
                      }
                    />
                    예
                  </label>
                  <label>
                    <input
                      type="radio"
                      name={`defect_q${index + 1}`}
                      value="no"
                      onChange={(e) =>
                        handleDefectQuestionChange(index + 1, e.target.value)
                      }
                    />
                    아니오
                  </label>
                </S.RadioGroup>
              </S.QuestionBox>
            ))}
          </S.Section>
        )}

        <S.Section>
          <S.Label>상세 설명</S.Label>
          <S.TextArea
            name="description"
            value={formData.description}
            onChange={handleChange}
            placeholder="사용 기간, 제공 구성품, 거래 방식 등을 자세히 설명해주세요"
          />
        </S.Section>

        <S.Section>
          <S.Label>가격</S.Label>
          <S.Input
            type="number"
            name="price"
            value={formData.price}
            onChange={handleChange}
            placeholder="판매 가격을 입력해주세요"
          />
        </S.Section>

        {formData.tradeType === "팔기" && (
          <>
            <S.Section>
              <S.Label>수량</S.Label>
              <S.Input
                type="number"
                name="sellInfo.quantity"
                value={formData.sellInfo.quantity}
                onChange={handleChange}
                placeholder="판매 수량을 입력해주세요"
              />
            </S.Section>

            <S.Section>
              <S.Label>위치</S.Label>
              <S.Input
                type="text"
                name="sellInfo.location"
                value={formData.sellInfo.location}
                onChange={handleChange}
                placeholder="거래 위치를 입력해주세요"
              />
            </S.Section>

            <S.Section>
              <S.Label>수거 시 받을 금액</S.Label>
              <S.Input
                type="number"
                name="sellInfo.pickupPrice"
                value={formData.sellInfo.pickupPrice}
                onChange={handleChange}
                placeholder="수거 시 받을 금액을 입력해주세요"
              />
            </S.Section>
          </>
        )}

        <S.SubmitButton type="submit">등록하기</S.SubmitButton>
      </S.Form>
    </S.Container>
  );
};

export default PostCreate;
