import React, { useState, useEffect, useRef } from "react";
import styled from "styled-components";
import Input from "../components/Input";
import {
  SignUpContainer,
  Logo,
  Form,
  Button,
  CheckboxContainer,
  CheckboxLabel,
  NicknameStatus,
} from "../styles/SignUpStyles";

const SignUp = () => {
  const [formData, setFormData] = useState({
    username: "",
    password: "",
    name: "",
    nickname: "",
    isBuyer: false,
  });

  const [errors, setErrors] = useState({});
  const [isNicknameAvailable, setIsNicknameAvailable] = useState(null);
  const nicknameTimeoutRef = useRef(null);

  const handleChange = (e) => {
    const { name, value, type, checked } = e.target;
    setFormData((prev) => ({
      ...prev,
      [name]: type === "checkbox" ? checked : value,
    }));

    if (name === "nickname") {
      setIsNicknameAvailable(null);
      if (nicknameTimeoutRef.current) {
        clearTimeout(nicknameTimeoutRef.current);
      }
    }
  };

  useEffect(() => {
    if (formData.nickname) {
      nicknameTimeoutRef.current = setTimeout(() => {
        // 실제로는 API 호출로 중복 확인
        // 임시로 랜덤하게 결정
        const isAvailable = Math.random() > 0.5;
        setIsNicknameAvailable(isAvailable);
      }, 1000);
    }

    return () => {
      if (nicknameTimeoutRef.current) {
        clearTimeout(nicknameTimeoutRef.current);
      }
    };
  }, [formData.nickname]);

  const validateForm = () => {
    const newErrors = {};

    if (!formData.username) newErrors.username = "아이디를 입력해주세요";
    if (!formData.password) newErrors.password = "비밀번호를 입력해주세요";
    if (!formData.name) newErrors.name = "이름을 입력해주세요";
    if (!formData.nickname) newErrors.nickname = "닉네임을 입력해주세요";
    if (formData.nickname.length > 10)
      newErrors.nickname = "닉네임은 10자 이내로 입력해주세요";
    if (isNicknameAvailable === false)
      newErrors.nickname = "이미 사용 중인 닉네임입니다";

    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    if (validateForm()) {
      // 회원가입 처리 로직
      console.log("회원가입 성공:", formData);
    }
  };

  return (
    <SignUpContainer>
      <Logo>Sallim</Logo>
      <Form onSubmit={handleSubmit}>
        <Input
          label="아이디"
          name="username"
          value={formData.username}
          onChange={handleChange}
          error={errors.username}
        />
        <Input
          label="비밀번호"
          name="password"
          type="password"
          value={formData.password}
          onChange={handleChange}
          error={errors.password}
        />
        <Input
          label="이름"
          name="name"
          value={formData.name}
          onChange={handleChange}
          error={errors.name}
        />
        <Input
          label="닉네임"
          name="nickname"
          value={formData.nickname}
          onChange={handleChange}
          error={errors.nickname}
          maxLength={10}
        />
        {formData.nickname && isNicknameAvailable !== null && (
          <NicknameStatus isAvailable={isNicknameAvailable}>
            {isNicknameAvailable
              ? "사용 가능한 닉네임입니다"
              : "이미 사용 중인 닉네임입니다"}
          </NicknameStatus>
        )}
        <CheckboxContainer>
          <input
            type="checkbox"
            name="isBuyer"
            checked={formData.isBuyer}
            onChange={handleChange}
          />
          <CheckboxLabel>바이어 여부</CheckboxLabel>
        </CheckboxContainer>
        <Button type="submit">회원가입</Button>
      </Form>
    </SignUpContainer>
  );
};

export default SignUp;
