import styled from "styled-components";

export const Container = styled.div`
  display: flex;
  flex-direction: column;
  height: 100vh;
  background-color: #f5f5f5;
`;

export const Header = styled.div`
  display: flex;
  align-items: center;
  padding: 16px;
  background-color: #fff;
  border-bottom: 1px solid #e0e0e0;
`;

export const BackButton = styled.button`
  background: none;
  border: none;
  padding: 8px;
  cursor: pointer;
  margin-right: 16px;

  img {
    width: 24px;
    height: 24px;
  }
`;

export const Title = styled.h1`
  font-size: 18px;
  font-weight: 600;
  color: #000;
  margin: 0;
`;

export const ChatArea = styled.div`
  flex: 1;
  overflow-y: auto;
  padding: 16px;
  display: flex;
  flex-direction: column;
  gap: 8px;
`;

export const DateDivider = styled.div`
  display: flex;
  justify-content: center;
  margin: 16px 0;

  span {
    background-color: rgba(0, 0, 0, 0.1);
    padding: 4px 12px;
    border-radius: 16px;
    font-size: 12px;
    color: #666;
  }
`;

export const Message = styled.div`
  display: flex;
  flex-direction: column;
  align-items: ${(props) => (props.isMine ? "flex-end" : "flex-start")};
  margin: 4px 0;
`;

export const MessageContent = styled.div`
  max-width: ${(props) => (props.isMine ? "60%" : "70%")};
  padding: 8px 12px;
  border-radius: 12px;
  background-color: ${(props) => (props.isMine ? "#FFE812" : "#fff")};
  position: relative;
  margin-bottom: 4px;

  &::before {
    content: "";
    position: absolute;
    top: 0;
    ${(props) => (props.isMine ? "right: -8px" : "left: -8px")};
    width: 0;
    height: 0;
    border-style: solid;
    border-width: 8px 0 8px 8px;
    border-color: ${(props) =>
      props.isMine
        ? "#FFE812 transparent transparent"
        : "#fff transparent transparent"};
    transform: ${(props) =>
      props.isMine ? "rotate(90deg)" : "rotate(-90deg)"};
  }
`;

export const MessageTime = styled.span`
  font-size: 11px;
  color: #999;
  margin-top: 2px;
`;

export const InputArea = styled.form`
  display: flex;
  align-items: center;
  padding: 16px;
  background-color: #fff;
  border-top: 1px solid #e0e0e0;
  gap: 8px;
`;

export const Input = styled.input`
  flex: 1;
  padding: 12px;
  border: 1px solid #e0e0e0;
  border-radius: 20px;
  font-size: 14px;
  outline: none;

  &:focus {
    border-color: #ffe812;
  }
`;

export const SendButton = styled.button`
  background-color: #ffe812;
  border: none;
  border-radius: 20px;
  min-width: 60px;
  height: 40px;
  display: flex;
  align-items: center;
  justify-content: center;
  cursor: pointer;
  padding: 0 16px;

  img {
    width: 20px;
    height: 20px;
  }

  &:hover {
    background-color: #ffd700;
  }
`;

export const MessageList = styled.div`
  flex: 1;
  overflow-y: auto;
  padding: 20px;
  display: flex;
  flex-direction: column;
  gap: 10px;
`;

export const MessageItem = styled.div`
  display: flex;
  flex-direction: column;
  align-items: ${(props) => (props.isMine ? "flex-end" : "flex-start")};
  max-width: 70%;
  align-self: ${(props) => (props.isMine ? "flex-end" : "flex-start")};
`;

export const MessageBubble = styled.div`
  background-color: ${(props) => (props.isMine ? "#007AFF" : "#E9ECEF")};
  color: ${(props) => (props.isMine ? "#FFFFFF" : "#000000")};
  padding: 12px 16px;
  border-radius: 16px;
  border-bottom-right-radius: ${(props) => (props.isMine ? "4px" : "16px")};
  border-bottom-left-radius: ${(props) => (props.isMine ? "16px" : "4px")};
  word-wrap: break-word;
  max-width: 100%;
`;

export const InputSection = styled.div`
  padding: 16px;
  background-color: #ffffff;
  border-top: 1px solid #e9ecef;
  display: flex;
  align-items: center;
  gap: 12px;
`;

export const MessageInput = styled.input`
  flex: 1;
  padding: 12px;
  border: 1px solid #e9ecef;
  border-radius: 20px;
  outline: none;
  font-size: 14px;

  &:focus {
    border-color: #007aff;
  }

  &::placeholder {
    color: #adb5bd;
  }
`;
