import React from "react";
import Header from "../../components/common/Header/Header";
import Navbar from "../../components/common/Navbar/Navbar";
import {
  Container,
  ChatListWrapper,
  ChatItem,
  ProfileCircle,
  ChatInfo,
  ChatTitleRow,
  ChatTitle,
  ChatTime,
  ChatMeta,
  ChatPreview,
  UnreadDot,
} from "./styled";

// 더미 데이터 예시
const chatList = [
  {
    id: 1,
    nickname: "Sarah Wilson",
    postTitle: "Vintage Camera Listing",
    lastMessage: "Hey, is this still available?",
    lastTime: "14:34",
    unread: false,
  },
  {
    id: 2,
    nickname: "Mike Johnson",
    postTitle: "Mountain Bike For Sale",
    lastMessage: "Can we meet tomorrow at 2 PM?",
    lastTime: "어제",
    unread: true,
  },
  {
    id: 3,
    nickname: "Emma Davis",
    postTitle: "iPhone 13 Pro",
    lastMessage: "Thank you for the quick response!",
    lastTime: "월",
    unread: false,
  },
];

const getInitial = (nickname) => nickname.charAt(0).toUpperCase();

const ChatList = () => {
  return (
    <Container>
      <Header />
      <ChatListWrapper>
        {chatList.map((chat) => (
          <ChatItem key={chat.id}>
            <ProfileCircle>{getInitial(chat.nickname)}</ProfileCircle>
            <ChatInfo>
              <ChatTitleRow>
                <ChatTitle>{chat.nickname}</ChatTitle>
                <ChatTime>{chat.lastTime}</ChatTime>
              </ChatTitleRow>
              <div style={{ color: "#888", fontSize: 13 }}>
                {chat.postTitle}
              </div>
              <ChatPreview>{chat.lastMessage}</ChatPreview>
            </ChatInfo>
            {chat.unread && <UnreadDot />}
          </ChatItem>
        ))}
      </ChatListWrapper>
      <Navbar />
    </Container>
  );
};

export default ChatList;
