import styled from "styled-components";

export const Container = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  min-height: 100vh;
  padding: 20px;
  background-color: #fff;
`;

export const Title = styled.h1`
  font-size: 24px;
  font-weight: 600;
  margin-bottom: 32px;
  color: #333;
`;

export const Form = styled.form`
  width: 100%;
  max-width: 320px;
  display: flex;
  flex-direction: column;
  gap: 16px;
`;

export const Input = styled.input`
  width: 100%;
  padding: 12px 16px;
  border: 1px solid #e0e0e0;
  border-radius: 8px;
  font-size: 16px;
  outline: none;

  &:focus {
    border-color: #9fb3df;
  }

  &::placeholder {
    color: #999;
  }
`;

export const Button = styled.button`
  width: 100%;
  padding: 12px;
  background-color: #9fb3df;
  color: white;
  border: none;
  border-radius: 8px;
  font-size: 16px;
  font-weight: 600;
  cursor: pointer;
  transition: background-color 0.2s;

  &:hover {
    background-color: #8ca3cf;
  }
`;

export const LinkButton = styled.button`
  background: none;
  border: none;
  color: #666;
  font-size: 14px;
  margin-top: 16px;
  cursor: pointer;
  text-decoration: underline;

  &:hover {
    color: #333;
  }
`;
