import styled from "styled-components";

export const Container = styled.div`
  width: 100%;
  min-height: 100vh;
  background: #f7faff;
  display: flex;
  flex-direction: column;
`;

export const Header = styled.div`
  width: 100%;
  height: 56px;
  background: #fff;
  display: flex;
  align-items: center;
  justify-content: center;
  border-bottom: 1px solid #e0e0e0;
  position: sticky;
  top: 0;
  z-index: 10;
`;

export const Title = styled.div`
  font-size: 18px;
  font-weight: 600;
  color: #222;
  cursor: pointer;
  &:hover {
    color: #4f8cff;
    text-decoration: underline;
  }
`;

export const MessageList = styled.div`
  flex: 1;
  display: flex;
  flex-direction: column;
  padding: 16px 12px 0 12px;
  overflow-y: auto;
`;

export const MessageItem = styled.div`
  display: flex;
  flex-direction: ${({ isMe }) => (isMe ? "row-reverse" : "row")};
  align-items: flex-end;
  margin-bottom: 10px;
`;

export const MessageBubble = styled.div`
  max-width: 70%;
  background: ${({ isMe }) => (isMe ? "#4f8cff" : "#fff")};
  color: ${({ isMe }) => (isMe ? "#fff" : "#222")};
  padding: 10px 14px;
  border-radius: 18px;
  border-bottom-right-radius: ${({ isMe }) => (isMe ? "4px" : "18px")};
  border-bottom-left-radius: ${({ isMe }) => (isMe ? "18px" : "4px")};
  font-size: 15px;
  box-shadow: 0 1px 4px rgba(0, 0, 0, 0.04);
  word-break: break-word;
`;

export const MessageTime = styled.div`
  font-size: 11px;
  color: #aaa;
  margin: 0 8px;
  align-self: flex-end;
`;

export const InputSection = styled.div`
  width: 100%;
  background: #fff;
  border-top: 1px solid #e0e0e0;
  display: flex;
  align-items: center;
  padding: 10px;
`;

export const MessageInput = styled.input`
  flex: 1;
  border: none;
  outline: none;
  font-size: 15px;
  padding: 10px 12px;
  border-radius: 20px;
  background: #f0f4fa;
  margin-right: 8px;
`;

export const SendButton = styled.button`
  background: #4f8cff;
  color: #fff;
  border: none;
  border-radius: 18px;
  padding: 8px 18px;
  font-size: 15px;
  font-weight: 600;
  cursor: pointer;
  flex-shrink: 0;
  transition: background 0.2s;
  &:hover {
    background: #2563d6;
  }
`;
