import styled from "styled-components";

export const Container = styled.div`
  padding: 76px 16px;
  background-color: #f5f5f5;
  min-height: 100vh;
`;

export const Header = styled.div`
  display: flex;
  justify-content: space-between;
  align-items: center;
  margin-bottom: 24px;
`;

export const Title = styled.h1`
  font-size: 1.5rem;
  font-weight: 600;
  color: #333;
`;

export const FilterButton = styled.button`
  padding: 8px 16px;
  background-color: white;
  border: 1px solid #e0e0e0;
  border-radius: 8px;
  font-size: 0.9rem;
  color: #666;
  cursor: pointer;
`;

export const CategoryContainer = styled.div`
  background: white;
  padding: 20px;
  border-radius: 12px;
  margin-bottom: 24px;
  display: flex;
  flex-direction: column;
  gap: 16px;
`;

export const CategorySection = styled.div`
  display: flex;
  flex-direction: column;
  gap: 12px;
`;

export const CategoryTitle = styled.h2`
  font-size: 1.1rem;
  font-weight: 600;
  color: #333;
`;

export const CategoryList = styled.div`
  display: flex;
  gap: 12px;
  flex-wrap: wrap;
`;

export const CategoryItem = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  gap: 8px;
  padding: 12px;
  border-radius: 8px;
  cursor: pointer;
  background-color: ${(props) =>
    props.$isSelected ? "#f0f4ff" : "transparent"};
  border: 1px solid ${(props) => (props.$isSelected ? "#9FB3DF" : "#e0e0e0")};

  &:hover {
    background-color: #f8f9ff;
  }
`;

export const CategoryIcon = styled.img`
  width: 24px;
  height: 24px;
`;

export const CategoryName = styled.span`
  font-size: 0.9rem;
  color: #333;
`;

export const Separator = styled.div`
  height: 1px;
  background-color: #e0e0e0;
  margin: 8px 0;
`;

export const PostList = styled.div`
  display: flex;
  flex-direction: column;
  gap: 16px;
`;

export const PostItem = styled.div`
  display: flex;
  gap: 16px;
  padding: 16px;
  background: white;
  border-radius: 12px;
  cursor: pointer;
`;

export const PostImage = styled.img`
  width: 100px;
  height: 100px;
  border-radius: 8px;
  object-fit: cover;
`;

export const PostInfo = styled.div`
  display: flex;
  flex-direction: column;
  gap: 8px;
  flex: 1;
`;

export const PostTitle = styled.h3`
  font-size: 1rem;
  font-weight: 600;
  color: #333;
`;

export const PostPrice = styled.p`
  font-size: 1.1rem;
  font-weight: 600;
  color: #333;
`;

export const PostLocation = styled.p`
  font-size: 0.9rem;
  color: #666;
`;
