import styled from "styled-components";

export const Container = styled.div`
  padding: 20px;
  margin-bottom: 60px;
  background-color: #fff;
`;

export const Form = styled.form`
  display: flex;
  flex-direction: column;
  gap: 24px;
`;

export const Section = styled.div`
  display: flex;
  flex-direction: column;
  gap: 12px;
`;

export const Label = styled.label`
  font-size: 16px;
  font-weight: 600;
  color: #333;
`;

export const Input = styled.input`
  width: 100%;
  padding: 12px;
  border: 1px solid #ddd;
  border-radius: 8px;
  font-size: 14px;
  &::placeholder {
    color: #999;
  }
  &:focus {
    outline: none;
    border-color: #007aff;
  }
`;

export const Select = styled.select`
  width: 100%;
  padding: 12px;
  border: 1px solid #ddd;
  border-radius: 8px;
  font-size: 14px;
  background-color: white;
  &:focus {
    outline: none;
    border-color: #007aff;
  }
`;

export const TextArea = styled.textarea`
  width: 100%;
  min-height: 120px;
  padding: 12px;
  border: 1px solid #ddd;
  border-radius: 8px;
  font-size: 14px;
  resize: vertical;
  &::placeholder {
    color: #999;
  }
  &:focus {
    outline: none;
    border-color: #007aff;
  }
`;

export const ImageUploadButton = styled.label`
  display: flex;
  align-items: center;
  justify-content: center;
  width: 100%;
  height: 120px;
  border: 2px dashed #ddd;
  border-radius: 8px;
  cursor: pointer;
  transition: all 0.2s ease;

  span {
    color: #666;
    font-size: 14px;
  }

  &:hover {
    border-color: #007aff;
    span {
      color: #007aff;
    }
  }
`;

export const QuestionBox = styled.div`
  padding: 16px;
  background-color: #f8f9fa;
  border-radius: 8px;
`;

export const Question = styled.p`
  margin-bottom: 12px;
  font-size: 14px;
  color: #333;
`;

export const RadioGroup = styled.div`
  display: flex;
  gap: 24px;

  label {
    display: flex;
    align-items: center;
    gap: 8px;
    font-size: 14px;
    color: #666;
    cursor: pointer;
  }

  input[type="radio"] {
    width: 16px;
    height: 16px;
    cursor: pointer;
  }
`;

export const SubmitButton = styled.button`
  width: 100%;
  padding: 16px;
  background-color: #007aff;
  color: white;
  border: none;
  border-radius: 8px;
  font-size: 16px;
  font-weight: 600;
  cursor: pointer;
  transition: background-color 0.2s ease;

  &:hover {
    background-color: #0056b3;
  }

  &:disabled {
    background-color: #ccc;
    cursor: not-allowed;
  }
`;
