import styled from "styled-components";

export const Container = styled.div`
  display: flex;
  flex-direction: column;
  height: 100vh;
  background: #fff;
`;

export const Header = styled.div`
  display: flex;
  align-items: center;
  padding: 16px;
  background-color: #9ec6f3;
  color: white;
`;

export const BackButton = styled.button`
  background: none;
  border: none;
  padding: 8px;
  cursor: pointer;
  color: white;

  img {
    width: 24px;
    height: 24px;
  }
`;

export const Title = styled.h1`
  margin: 0;
  font-size: 18px;
  font-weight: bold;
  flex: 1;
  text-align: center;
`;

export const ChatArea = styled.div`
  flex: 1;
  overflow-y: auto;
  padding: 16px;
  display: flex;
  flex-direction: column;
  gap: 16px;
`;

export const Message = styled.div`
  display: flex;
  flex-direction: ${(props) => (props.isMine ? "row-reverse" : "row")};
  align-items: flex-end;
  gap: 8px;
`;

export const MessageContent = styled.div`
  background-color: ${(props) => (props.isMine ? "#9EC6F3" : "#BDDDE4")};
  color: ${(props) => (props.isMine ? "white" : "#333")};
  padding: 12px 16px;
  border-radius: 16px;
  max-width: 70%;
  word-break: break-word;
`;

export const MessageTime = styled.span`
  font-size: 12px;
  color: #9fb3df;
`;

export const InputArea = styled.form`
  display: flex;
  gap: 8px;
  padding: 16px;
  background-color: #fff;
  border-top: 1px solid #eee;
`;

export const Input = styled.input`
  flex: 1;
  padding: 12px;
  border: 1px solid #bddde4;
  border-radius: 20px;
  font-size: 14px;

  &:focus {
    outline: none;
    border-color: #9ec6f3;
  }
`;

export const SendButton = styled.button`
  background-color: #9ec6f3;
  color: white;
  border: none;
  border-radius: 50%;
  width: 40px;
  height: 40px;
  display: flex;
  align-items: center;
  justify-content: center;
  cursor: pointer;

  &:hover {
    background-color: #9fb3df;
  }

  img {
    width: 20px;
    height: 20px;
  }
`;

export const MessageList = styled.div`
  flex: 1;
  overflow-y: auto;
  padding: 20px;
  display: flex;
  flex-direction: column;
  gap: 10px;
`;

export const MessageItem = styled.div`
  display: flex;
  flex-direction: column;
  align-items: ${(props) => (props.isMine ? "flex-end" : "flex-start")};
  max-width: 70%;
  align-self: ${(props) => (props.isMine ? "flex-end" : "flex-start")};
`;

export const MessageBubble = styled.div`
  background-color: ${(props) => (props.isMine ? "#007AFF" : "#E9ECEF")};
  color: ${(props) => (props.isMine ? "#FFFFFF" : "#000000")};
  padding: 12px 16px;
  border-radius: 16px;
  border-bottom-right-radius: ${(props) => (props.isMine ? "4px" : "16px")};
  border-bottom-left-radius: ${(props) => (props.isMine ? "16px" : "4px")};
  word-wrap: break-word;
  max-width: 100%;
`;

export const InputSection = styled.div`
  padding: 16px;
  background-color: #ffffff;
  border-top: 1px solid #e9ecef;
  display: flex;
  align-items: center;
  gap: 12px;
`;

export const MessageInput = styled.input`
  flex: 1;
  padding: 12px;
  border: 1px solid #e9ecef;
  border-radius: 20px;
  outline: none;
  font-size: 14px;

  &:focus {
    border-color: #007aff;
  }

  &::placeholder {
    color: #adb5bd;
  }
`;
