import styled from "styled-components";

const Container = styled.div`
  padding-bottom: 80px;
  background: #fff;
  min-height: 100vh;
`;
const Title = styled.h2`
  font-size: 1.2rem;
  font-weight: bold;
  margin: 24px 0 16px 16px;
`;
const CardList = styled.div`
  display: flex;
  gap: 12px;
  overflow-x: auto;
  padding: 0 16px;
`;
const Card = styled.div`
  min-width: 120px;
  max-width: 140px;
  background: #f5f5f5;
  border-radius: 8px;
  padding: 8px;
  display: flex;
  flex-direction: column;
  align-items: center;
`;
const ImageBox = styled.div`
  width: 100%;
  height: 70px;
  background: #ccc;
  border-radius: 4px;
  margin-bottom: 8px;
`;
const CardTitle = styled.div`
  font-size: 0.95rem;
  font-weight: 500;
  margin-bottom: 2px;
  text-align: center;
  white-space: nowrap;
  overflow: hidden;
  text-overflow: ellipsis;
  width: 100%;
`;
const CardPrice = styled.div`
  font-size: 0.9rem;
  color: #444;
  text-align: center;
`;
