import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import Navbar from "../../../components/common/Navbar/Navbar";
import Header from "../../../components/common/Header/Header";
import * as S from "./styled";

const Mypage = () => {
  const navigate = useNavigate();
  // 상태: 수정모드 여부, 프로필 정보
  const [editMode, setEditMode] = useState(false);
  const [profile, setProfile] = useState({ name: "홍길동", nickname: "HGD" });
  const [form, setForm] = useState(profile);
  const [posts, setPosts] = useState([]);

  useEffect(() => {
    const savedPosts = localStorage.getItem("posts");
    if (savedPosts) {
      setPosts(JSON.parse(savedPosts));
    }
  }, []);

  const handleEdit = (e) => {
    e.stopPropagation();
    setForm(profile);
    setEditMode(true);
  };

  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleSave = (e) => {
    e.stopPropagation();
    setProfile(form);
    setEditMode(false);
  };

  const scrappedPosts = posts.filter((post) => post.isScraped).slice(0, 3);

  return (
    <S.Container>
      <Header />
      <S.ProfileSection>
        <S.ProfileCircle>
          {profile.nickname.charAt(0).toUpperCase()}
        </S.ProfileCircle>
        <S.ProfileInfo>
          {editMode ? (
            <>
              <input
                name="name"
                value={form.name}
                onChange={handleChange}
                style={{
                  marginBottom: 8,
                  padding: 4,
                  borderRadius: 4,
                  border: "1px solid #ccc",
                }}
              />
              <input
                name="nickname"
                value={form.nickname}
                onChange={handleChange}
                style={{
                  padding: 4,
                  borderRadius: 4,
                  border: "1px solid #ccc",
                }}
              />
            </>
          ) : (
            <>
              <div>이름: {profile.name}</div>
              <div>닉네임: {profile.nickname}</div>
            </>
          )}
        </S.ProfileInfo>
      </S.ProfileSection>
      {editMode ? (
        <S.EditButton onClick={handleSave}>저장</S.EditButton>
      ) : (
        <S.EditButton onClick={(e) => handleEdit(e)}>
          회원 정보 수정
        </S.EditButton>
      )}
      <S.Section>
        <S.SectionHeader>
          내가 쓴 글
          <S.Arrow onClick={() => navigate("/my-page/my-posts")}>▶</S.Arrow>
        </S.SectionHeader>
        <S.CardList>
          <S.Card>
            <S.ImageBox />
            <S.CardTitle>Vintage Chair</S.CardTitle>
            <S.CardPrice>₩45,000</S.CardPrice>
          </S.Card>
        </S.CardList>
      </S.Section>
      <S.Section>
        <S.SectionHeader>
          스크랩한 글
          <S.Arrow onClick={() => navigate("/my-page/scrapped")}>▶</S.Arrow>
        </S.SectionHeader>
        <S.CardList>
          {scrappedPosts.map((post) => (
            <S.Card
              key={post.id}
              onClick={() => navigate(`/post/detail/${post.id}`)}
            >
              <S.ImageBox>
                <img
                  src={post.imageUrl}
                  alt={post.title}
                  style={{ width: "100%", height: "100%", objectFit: "cover" }}
                />
              </S.ImageBox>
              <S.CardTitle>{post.title}</S.CardTitle>
              <S.CardPrice>{post.price.toLocaleString()}원</S.CardPrice>
            </S.Card>
          ))}
        </S.CardList>
      </S.Section>
      <Navbar />
    </S.Container>
  );
};

export default Mypage;
