import styled from "styled-components";

export const Container = styled.div`
  padding-top: 60px;
  background: #fff;
  min-height: 100vh;
`;

export const ProfileSection = styled.div`
  display: flex;
  align-items: center;
  gap: 16px;
  padding: 24px 16px 8px 16px;
`;

export const ProfileCircle = styled.div`
  width: 56px;
  height: 56px;
  border-radius: 50%;
  background: #bbb;
  color: #fff;
  font-size: 2rem;
  font-weight: bold;
  display: flex;
  align-items: center;
  justify-content: center;
`;

export const ProfileInfo = styled.div`
  display: flex;
  flex-direction: column;
  gap: 4px;
  font-size: 1rem;
`;

export const EditButton = styled.button`
  margin: 0 16px 16px 16px;
  width: calc(100% - 32px);
  padding: 10px 0;
  background: #222;
  color: #fff;
  border: none;
  border-radius: 8px;
  font-size: 1rem;
  cursor: pointer;
`;

export const Section = styled.div`
  margin: 0 0 24px 0;
  padding: 0 16px;
`;

export const SectionHeader = styled.div`
  font-weight: bold;
  font-size: 1.1rem;
  margin-bottom: 8px;
  display: flex;
  align-items: center;
  justify-content: space-between;
`;

export const Arrow = styled.span`
  font-size: 1.2rem;
  cursor: pointer;
  margin-left: 8px;
`;

export const CardList = styled.div`
  display: flex;
  gap: 12px;
  overflow-x: auto;
`;

export const Card = styled.div`
  min-width: 120px;
  max-width: 140px;
  background: #f5f5f5;
  border-radius: 8px;
  padding: 8px;
  display: flex;
  flex-direction: column;
  align-items: center;
`;

export const ImageBox = styled.div`
  width: 100%;
  height: 70px;
  background: #ccc;
  border-radius: 4px;
  margin-bottom: 8px;
`;

export const CardTitle = styled.div`
  font-size: 0.95rem;
  font-weight: 500;
  margin-bottom: 2px;
  text-align: center;
  white-space: nowrap;
  overflow: hidden;
  text-overflow: ellipsis;
  width: 100%;
`;

export const CardPrice = styled.div`
  font-size: 0.9rem;
  color: #444;
  text-align: center;
`;
