import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import {
  Container,
  Form,
  Section,
  Label,
  Input,
  Select,
  TextArea,
  ImageUploadButton,
  QuestionBox,
  Question,
  RadioGroup,
  SubmitButton,
  Divider,
} from "./styled";

const PostCreate = () => {
  const navigate = useNavigate();
  const [formData, setFormData] = useState({
    title: "",
    tradeType: "",
    category: "",
    images: [],
    modelName: "",
    specifications: {
      brand: "",
      capacity: "",
      powerConsumption: "",
      size: "",
    },
    defectAnswers: {},
    description: "",
    price: "",
    sellingInfo: {
      quantity: "",
      location: "",
      pickupPrice: "",
    },
    buyingInfo: {
      quantity: "",
      desiredPrice: "",
      deadline: "",
      condition: "",
    },
  });

  const categories = ["냉장고", "세탁기", "에어컨"];
  const tradeTypes = ["판매", "구매", "마감"];

  const defectQuestions = {
    냉장고: [
      "냉각 기능에 문제가 있나요?",
      "문이 제대로 닫히나요?",
      "내부 부품이 모두 있나요?",
    ],
    세탁기: [
      "세탁 기능에 문제가 있나요?",
      "배수가 잘 되나요?",
      "소음이 심한가요?",
    ],
    에어컨: [
      "냉방 기능에 문제가 있나요?",
      "실외기 상태는 어떤가요?",
      "필터 상태는 어떤가요?",
    ],
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    if (name.includes(".")) {
      const [parent, child] = name.split(".");
      setFormData((prev) => ({
        ...prev,
        [parent]: {
          ...prev[parent],
          [child]: value,
        },
      }));
    } else {
      setFormData((prev) => ({
        ...prev,
        [name]: value,
      }));
    }
  };

  const handleDefectQuestionChange = (question, answer) => {
    setFormData((prev) => ({
      ...prev,
      defectAnswers: {
        ...prev.defectAnswers,
        [question]: answer,
      },
    }));
  };

  const handleImageUpload = (e) => {
    const files = Array.from(e.target.files);
    if (files.length + formData.images.length > 5) {
      alert("최대 5장까지 업로드할 수 있습니다.");
      return;
    }

    const newImages = files.map((file) => URL.createObjectURL(file));
    setFormData((prev) => ({
      ...prev,
      images: [...prev.images, ...newImages],
    }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    console.log("Form submitted:", formData);
    navigate("/post");
  };

  return (
    <Container>
      <Form onSubmit={handleSubmit}>
        <Section>
          <Label>제목</Label>
          <Input
            type="text"
            name="title"
            value={formData.title}
            onChange={handleChange}
            placeholder="브랜드명, 제품명, 모델명을 포함해주세요"
            required
          />
        </Section>

        <Section>
          <Label>거래 유형</Label>
          <Select
            name="tradeType"
            value={formData.tradeType}
            onChange={handleChange}
            required
          >
            <option value="">선택하세요</option>
            {tradeTypes.map((type) => (
              <option key={type} value={type}>
                {type}
              </option>
            ))}
          </Select>
        </Section>

        {formData.tradeType && (
          <>
            <Section>
              <Label>카테고리</Label>
              <Select
                name="category"
                value={formData.category}
                onChange={handleChange}
                required
              >
                <option value="">선택하세요</option>
                {categories.map((category) => (
                  <option key={category} value={category}>
                    {category}
                  </option>
                ))}
              </Select>
            </Section>

            {formData.category && (
              <>
                <Divider />
                <Section>
                  <Label>제품 상태</Label>
                  <QuestionBox>
                    {defectQuestions[formData.category].map(
                      (question, index) => (
                        <div
                          key={index}
                          style={{
                            marginBottom:
                              index !==
                              defectQuestions[formData.category].length - 1
                                ? "16px"
                                : "0",
                          }}
                        >
                          <Question>{question}</Question>
                          <RadioGroup>
                            <label>
                              <input
                                type="radio"
                                name={`defect_${index}`}
                                value="yes"
                                onChange={() =>
                                  handleDefectQuestionChange(question, "yes")
                                }
                                checked={
                                  formData.defectAnswers[question] === "yes"
                                }
                                required
                              />
                              <span>예</span>
                            </label>
                            <label>
                              <input
                                type="radio"
                                name={`defect_${index}`}
                                value="no"
                                onChange={() =>
                                  handleDefectQuestionChange(question, "no")
                                }
                                checked={
                                  formData.defectAnswers[question] === "no"
                                }
                                required
                              />
                              <span>아니오</span>
                            </label>
                          </RadioGroup>
                        </div>
                      )
                    )}
                  </QuestionBox>
                </Section>
              </>
            )}

            {formData.tradeType === "판매" && (
              <>
                <Section>
                  <Label>제품 사진 (최대 5장)</Label>
                  <ImageUploadButton>
                    <input
                      type="file"
                      accept="image/*"
                      multiple
                      onChange={handleImageUpload}
                      style={{ display: "none" }}
                    />
                    <span>클릭하여 사진 업로드</span>
                  </ImageUploadButton>
                  {formData.images.length > 0 && (
                    <div
                      style={{ display: "flex", gap: "8px", marginTop: "8px" }}
                    >
                      {formData.images.map((image, index) => (
                        <img
                          key={index}
                          src={image}
                          alt={`Preview ${index + 1}`}
                          style={{
                            width: "80px",
                            height: "80px",
                            objectFit: "cover",
                            borderRadius: "4px",
                          }}
                        />
                      ))}
                    </div>
                  )}
                </Section>

                <Section>
                  <Label>모델명</Label>
                  <Input
                    type="text"
                    name="modelName"
                    value={formData.modelName}
                    onChange={handleChange}
                    placeholder="정확한 모델명을 입력해주세요"
                    required
                  />
                </Section>

                <Section>
                  <Label>제품 사양</Label>
                  <Input
                    type="text"
                    name="specifications.brand"
                    value={formData.specifications.brand}
                    onChange={handleChange}
                    placeholder="브랜드"
                    required
                  />
                  <Input
                    type="text"
                    name="specifications.capacity"
                    value={formData.specifications.capacity}
                    onChange={handleChange}
                    placeholder="용량"
                    required
                  />
                  <Input
                    type="text"
                    name="specifications.powerConsumption"
                    value={formData.specifications.powerConsumption}
                    onChange={handleChange}
                    placeholder="소비전력"
                    required
                  />
                  <Input
                    type="text"
                    name="specifications.size"
                    value={formData.specifications.size}
                    onChange={handleChange}
                    placeholder="크기"
                    required
                  />
                </Section>

                <Section>
                  <Label>상세 설명</Label>
                  <TextArea
                    name="description"
                    value={formData.description}
                    onChange={handleChange}
                    placeholder="제품의 사용 기간, 상태, 하자 여부 등 상세한 정보를 입력해주세요"
                    required
                  />
                </Section>

                <Section>
                  <Label>가격</Label>
                  <Input
                    type="number"
                    name="price"
                    value={formData.price}
                    onChange={handleChange}
                    placeholder="숫자만 입력"
                    required
                  />
                </Section>
              </>
            )}

            {formData.tradeType === "구매" && (
              <>
                <Section>
                  <Label>구매 정보</Label>
                  <Input
                    type="number"
                    name="buyingInfo.quantity"
                    value={formData.buyingInfo.quantity}
                    onChange={handleChange}
                    placeholder="수량"
                    required
                  />
                  <Input
                    type="number"
                    name="buyingInfo.desiredPrice"
                    value={formData.buyingInfo.desiredPrice}
                    onChange={handleChange}
                    placeholder="희망 가격"
                    required
                  />
                  <Input
                    type="date"
                    name="buyingInfo.deadline"
                    value={formData.buyingInfo.deadline}
                    onChange={handleChange}
                    placeholder="마감 기한"
                    required
                  />
                  <TextArea
                    name="buyingInfo.condition"
                    value={formData.buyingInfo.condition}
                    onChange={handleChange}
                    placeholder="제품 조건을 입력해주세요"
                    required
                  />
                </Section>
              </>
            )}
          </>
        )}

        <SubmitButton type="submit">등록하기</SubmitButton>
      </Form>
    </Container>
  );
};

export default PostCreate;
