import React, { useState, useMemo } from "react";
import { useNavigate, useParams } from "react-router-dom";
import * as S from "./styled";
import scrapOn from "../../assets/scrap-on.svg";
import scrapOff from "../../assets/scrap-off.svg";
// import { ReactComponent as EditIcon } from "../../assets/icons/edit.svg";
// import { ReactComponent as DeleteIcon } from "../../assets/icons/delete.svg";

const PostDetail = () => {
  const navigate = useNavigate();
  const { id } = useParams();
  const [isScraped, setIsScraped] = useState(false);
  const [comment, setComment] = useState("");
  const [comments, setComments] = useState([]);

  // 랜덤 색상 생성 함수
  const getRandomColor = () => {
    const colors = [
      "#FF9AA2",
      "#FFB7B2",
      "#FFDAC1",
      "#E2F0CB",
      "#B5EAD7",
      "#C7CEEA",
      "#9FB3DF",
      "#B8B3E9",
      "#D4A5A5",
      "#9CADCE",
    ];
    return colors[Math.floor(Math.random() * colors.length)];
  };

  // Mock data - 실제로는 API에서 받아와야 함
  const savedPosts = localStorage.getItem("posts");
  const posts = savedPosts ? JSON.parse(savedPosts) : [];
  const post = posts.find((p) => p.id === Number(id)) || {
    id: 1,
    title: "삼성 비스포크 냉장고",
    author: "현재 사용자",
    authorId: "current-user",
    isAuthor: true,
    type: "sell",
    status: "available",
    category: "냉장고",
    images: [],
    modelName: "RF85B902221",
    specifications: {
      brand: "삼성",
      capacity: "850L",
      powerConsumption: "380kWh",
      size: "908 x 1853 x 934mm",
    },
    defectQuestions: {
      cooling: "정상",
      noise: "약간의 소음",
      exterior: "미세 스크래치",
    },
    description: "2년 사용한 비스포크 냉장고입니다.",
    price: 2450000,
    sellingInfo: {
      quantity: 1,
      location: "서울시 강남구",
      pickupAvailable: true,
      currentParticipants: 0,
    },
    createdAt: "2024-03-15",
  };

  const profileColor = useMemo(() => getRandomColor(), []);
  const initial = post?.author ? post.author.charAt(0) : "?";

  const handleScrap = () => {
    setIsScraped(!isScraped);
  };

  //   const handleEdit = () => {
  //     navigate(`/post/edit/${id}`);
  //   };

  //   const handleDelete = () => {
  //     if (window.confirm("정말로 삭제하시겠습니까?")) {
  //       // API call to delete post
  //       navigate("/post");
  //     }
  //   };

  const handleChat = () => {
    navigate(`/chat/${post.author}`);
  };

  const handleCommentSubmit = (e) => {
    e.preventDefault();
    if (comment.trim() && comment.length <= 1000) {
      setComments([
        ...comments,
        {
          id: Date.now(),
          author: "현재 사용자",
          content: comment,
          createdAt: new Date().toISOString(),
        },
      ]);
      setComment("");
    }
  };

  return (
    <S.Container>
      <S.Header>
        <S.Title>{post.title}</S.Title>
        <S.ActionButtons>
          <S.IconButton
            src={isScraped ? scrapOn : scrapOff}
            alt="스크랩 아이콘"
            onClick={handleScrap}
          />

          {post.isAuthor && (
            <>
              {/* <S.IconButton onClick={handleEdit}>
                <EditIcon />
              </S.IconButton>
              <S.IconButton onClick={handleDelete}>
                <DeleteIcon />
              </S.IconButton> */}
            </>
          )}
        </S.ActionButtons>
      </S.Header>

      <S.Content>
        <S.ImageSection>
          {/* Image carousel will be added here */}
        </S.ImageSection>

        <S.InfoSection>
          <S.AuthorSection>
            <S.AuthorInfo>
              <S.ProfileInitial backgroundColor={profileColor}>
                {initial}
              </S.ProfileInitial>
              <S.Author>{post.author}</S.Author>
            </S.AuthorInfo>
            <S.ChatButton onClick={handleChat}>채팅하기</S.ChatButton>
          </S.AuthorSection>

          <S.SpecificationSection>
            <S.Label>제품 정보</S.Label>
            <S.SpecificationGrid>
              <S.SpecificationItem>
                <span>브랜드</span>
                <span>{post.specifications.brand}</span>
              </S.SpecificationItem>
              <S.SpecificationItem>
                <span>용량</span>
                <span>{post.specifications.capacity}</span>
              </S.SpecificationItem>
              <S.SpecificationItem>
                <span>소비전력</span>
                <span>{post.specifications.powerConsumption}</span>
              </S.SpecificationItem>
              <S.SpecificationItem>
                <span>크기</span>
                <span>{post.specifications.size}</span>
              </S.SpecificationItem>
            </S.SpecificationGrid>
          </S.SpecificationSection>

          <S.DefectSection>
            <S.Label>상태 정보</S.Label>
            <S.DefectGrid>
              <S.DefectItem>
                <span>냉각 기능</span>
                <span>{post.defectQuestions.cooling}</span>
              </S.DefectItem>
              <S.DefectItem>
                <span>소음</span>
                <span>{post.defectQuestions.noise}</span>
              </S.DefectItem>
              <S.DefectItem>
                <span>외관</span>
                <span>{post.defectQuestions.exterior}</span>
              </S.DefectItem>
            </S.DefectGrid>
          </S.DefectSection>

          {post.type === "sell" && (
            <S.SellingInfoSection>
              <S.Label>거래 정보</S.Label>
              <S.SellingInfoGrid>
                <S.SellingInfoItem>
                  <span>수량</span>
                  <span>{post.sellingInfo.quantity}개</span>
                </S.SellingInfoItem>
                <S.SellingInfoItem>
                  <span>위치</span>
                  <span>{post.sellingInfo.location}</span>
                </S.SellingInfoItem>
                <S.SellingInfoItem>
                  <span>수거 여부</span>
                  <span>
                    {post.sellingInfo.pickupAvailable ? "가능" : "불가능"}
                  </span>
                </S.SellingInfoItem>
                <S.SellingInfoItem>
                  <span>현재 인원</span>
                  <span>{post.sellingInfo.currentParticipants}명</span>
                </S.SellingInfoItem>
              </S.SellingInfoGrid>
            </S.SellingInfoSection>
          )}

          <S.Description>
            <S.Label>상세 설명</S.Label>
            <S.DescriptionText>{post.description}</S.DescriptionText>
          </S.Description>

          <S.Price>
            <span>가격</span>
            <span>{post.price.toLocaleString()}원</span>
          </S.Price>
        </S.InfoSection>

        <S.CommentSection>
          <S.Label>댓글</S.Label>
          <S.CommentForm onSubmit={handleCommentSubmit}>
            <S.CommentInput
              value={comment}
              onChange={(e) => setComment(e.target.value)}
              maxLength={1000}
              placeholder="댓글을 입력하세요 (최대 1000자)"
            />
            <S.CommentButton type="submit">등록</S.CommentButton>
          </S.CommentForm>
          <S.CommentList>
            {comments.map((comment) => (
              <S.CommentItem key={comment.id}>
                <S.CommentHeader>
                  <S.CommentAuthor>{comment.author}</S.CommentAuthor>
                  <S.CommentDate>
                    {new Date(comment.createdAt).toLocaleDateString()}
                  </S.CommentDate>
                </S.CommentHeader>
                <S.CommentContent>{comment.content}</S.CommentContent>
              </S.CommentItem>
            ))}
          </S.CommentList>
        </S.CommentSection>
      </S.Content>
    </S.Container>
  );
};

export default PostDetail;
