import styled from "styled-components";

export const InputContainer = styled.div`
  width: 80%;
  margin-bottom: 0.5rem;
`;

export const StyledInput = styled.input`
  width: 100%;
  padding: 1rem;
  border: 1px solid #bddde4;
  border-radius: 8px;
  font-size: 1rem;
  background-color: white;
  transition: all 0.1s;
  margin-top: 1px;

  &:focus {
    outline: none;
    border-color: #9fb3df;
    box-shadow: 0 0 0 2px rgba(159, 179, 223, 0.1);
  }

  &:disabled {
    background-color: #fdfaf6;
    cursor: not-allowed;
  }

  &::placeholder {
    color: #bddde4;
  }
`;

export const ErrorMessage = styled.p`
  color: #ff6b6b;
  font-size: 0.875rem;
  margin-top: 0.5rem;
  padding-left: 0.5rem;
`;
