import styled from "styled-components";

export const LoginContainer = styled.div`
  width: 100%;
  min-height: 100vh;
  padding: 0;
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
`;

export const Form = styled.form`
  width: 100%;
  display: flex;
  flex-direction: column;
  align-items: center;
  gap: 1.5rem;
  padding: 2rem 1.5rem;
  border-radius: 20px 20px 0 0;
`;

export const Input = styled.input`
  width: 100%;
  padding: 12px 16px;
  border: 1px solid #e0e0e0;
  border-radius: 8px;
  font-size: 16px;
  outline: none;

  &:focus {
    border-color: #9fb3df;
  }

  &::placeholder {
    color: #999;
  }
`;

export const Button = styled.button`
  width: 100%;
  padding: 12px;
  background-color: #9fb3df;
  color: white;
  border: none;
  border-radius: 8px;
  font-size: 16px;
  font-weight: 600;
  cursor: pointer;
  transition: background-color 0.2s;

  &:hover {
    background-color: #8ca3cf;
  }

  &[type="button"] {
    background-color: white;
    color: #9fb3df;
    border: 1px solid #9fb3df;

    &:hover {
      background-color: #f8f9ff;
    }
  }
`;

export const LinkButton = styled.button`
  background: none;
  border: none;
  color: #666;
  font-size: 14px;
  margin-top: 16px;
  cursor: pointer;
  text-decoration: underline;

  &:hover {
    color: #333;
  }
`;
