import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import * as S from "./styled";
import Header from "../../components/common/Header/Header";
import Navbar from "../../components/common/Navbar/Navbar";

const PostCreate = () => {
  const navigate = useNavigate();
  const [step, setStep] = useState(1);
  const [formData, setFormData] = useState({
    title: "",
    tradeType: "", // 판매, 구매, 마감
    category: "", // 냉장고, 세탁기, 에어컨
    images: [],
    modelName: "",
    specifications: {
      brand: "",
      capacity: "",
      powerConsumption: "",
      size: "",
    },
    defectAnswers: {},
    description: "",
    price: "",
    // 판매 시 추가 정보
    sellingInfo: {
      quantity: "1",
      location: "",
      pickupPrice: "",
    },
    // 구매 시 추가 정보
    buyingInfo: {
      quantity: "3",
      desiredPrice: "",
      deadline: "",
      condition: "",
    },
  });

  const categories = ["냉장고", "에어컨", "세탁기"];
  const tradeTypes = ["판매", "구매", "마감"];

  const defectQuestions = {
    냉장고: [
      "냉동실 내부에 성에가 과도하게 생기거나 물이 새는 현상이 있었나요?",
      "냉장고 내부 온도가 설정한 대로 유지되지 않는 경우가 있었나요?",
      "냉장고 작동 중 소음이 지속적으로 들려 불편함을 느낀 적이 있나요?",
    ],
    에어컨: [
      "냉방 성능이 떨어지거나 실내 온도가 일정하게 유지되지 않는 문제가 있었나요?",
      "실외기에서 비정상적인 소음이나 진동이 발생한 적이 있나요?",
      "실내기에서 물이 새거나 냄새가 나는 현상이 있었나요?",
    ],
    세탁기: [
      "세탁 또는 탈수 중 비정상적인 소음이나 진동이 있었나요?",
      "물 배출이 잘 되지 않거나 누수가 있었나요?",
      "세탁이 완료된 후에도 옷에서 냄새가 나거나 세제가 남아있는 경우가 있었나요?",
    ],
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    if (name.includes(".")) {
      const [parent, child] = name.split(".");
      setFormData((prev) => ({
        ...prev,
        [parent]: {
          ...prev[parent],
          [child]: value,
        },
      }));
    } else {
      setFormData((prev) => ({
        ...prev,
        [name]: value,
      }));
    }
  };

  const handleTradeTypeChange = (e) => {
    const value = e.target.value;
    if (value === "구매") {
      // TODO: 바이어 인증 여부 확인
      const isBuyer = false; // API 호출로 실제 바이어 인증 여부 확인 필요
      if (!isBuyer) {
        alert("바이어 인증이 필요합니다.");
        return;
      }
    }
    setFormData((prev) => ({
      ...prev,
      tradeType: value,
    }));
    setStep(2);
  };

  const handleDefectQuestionChange = (questionNumber, value) => {
    setFormData((prev) => ({
      ...prev,
      defectAnswers: {
        ...prev.defectAnswers,
        [`q${questionNumber}`]: value,
      },
    }));
  };

  const handleImageUpload = (e) => {
    const files = Array.from(e.target.files);
    if (files.length > 5) {
      alert("최대 5개의 이미지만 업로드할 수 있습니다.");
      return;
    }

    setFormData((prev) => ({
      ...prev,
      images: files,
    }));

    // TODO: AI 이미지 분석 API 호출
    // 임시로 더미 데이터 설정
    setFormData((prev) => ({
      ...prev,
      specifications: {
        brand: "삼성",
        capacity: "500L",
        powerConsumption: "1등급",
        size: "180 x 90 x 70 cm",
      },
    }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    // TODO: API 호출하여 데이터 저장
    console.log("Form submitted:", formData);
    navigate("/postlist");
  };

  return (
    <>
      <Header />
      <S.Container>
        <S.Form onSubmit={handleSubmit}>
          <S.Section>
            <S.Label>제목</S.Label>
            <S.Input
              type="text"
              name="title"
              value={formData.title}
              onChange={handleChange}
              placeholder="브랜드, 제품 종류, 모델명을 포함하여 작성해주세요"
            />
          </S.Section>

          <S.Section>
            <S.Label>거래 유형</S.Label>
            <S.Select
              name="tradeType"
              value={formData.tradeType}
              onChange={handleTradeTypeChange}
            >
              <option value="">선택해주세요</option>
              {tradeTypes.map((type) => (
                <option key={type} value={type}>
                  {type}
                </option>
              ))}
            </S.Select>
          </S.Section>

          {formData.tradeType && (
            <>
              <S.Section>
                <S.Label>제품 종류</S.Label>
                <S.Select
                  name="category"
                  value={formData.category}
                  onChange={handleChange}
                >
                  <option value="">선택해주세요</option>
                  {categories.map((category) => (
                    <option key={category} value={category}>
                      {category}
                    </option>
                  ))}
                </S.Select>
              </S.Section>

              {formData.tradeType === "판매" && (
                <>
                  <S.Section>
                    <S.Label>제품 사진</S.Label>
                    <input
                      type="file"
                      accept="image/*"
                      multiple
                      onChange={handleImageUpload}
                      style={{ display: "none" }}
                      id="imageUpload"
                    />
                    <S.ImageUploadButton as="label" htmlFor="imageUpload">
                      <span>사진 추가하기 (최대 5장)</span>
                    </S.ImageUploadButton>
                  </S.Section>

                  {formData.images.length > 0 && (
                    <S.Section>
                      <S.Label>제품 정보</S.Label>
                      <S.Input
                        type="text"
                        name="modelName"
                        value={formData.modelName}
                        onChange={handleChange}
                        placeholder="정확한 모델명을 입력해주세요"
                      />
                      <S.Input
                        type="text"
                        name="specifications.brand"
                        value={formData.specifications.brand}
                        onChange={handleChange}
                        placeholder="브랜드"
                      />
                      <S.Input
                        type="text"
                        name="specifications.capacity"
                        value={formData.specifications.capacity}
                        onChange={handleChange}
                        placeholder="용량 (예: 500L, 18kg)"
                      />
                      <S.Input
                        type="text"
                        name="specifications.powerConsumption"
                        value={formData.specifications.powerConsumption}
                        onChange={handleChange}
                        placeholder="전력 소비량 (예: 1등급, 500W)"
                      />
                      <S.Input
                        type="text"
                        name="specifications.size"
                        value={formData.specifications.size}
                        onChange={handleChange}
                        placeholder="크기 (가로 x 세로 x 높이)"
                      />
                    </S.Section>
                  )}

                  {formData.category && (
                    <S.Section>
                      <S.Label>하자 정보</S.Label>
                      {defectQuestions[formData.category].map(
                        (question, index) => (
                          <S.QuestionBox key={index}>
                            <S.Question>{question}</S.Question>
                            <S.RadioGroup>
                              <label>
                                <input
                                  type="radio"
                                  name={`defect_q${index + 1}`}
                                  value="yes"
                                  onChange={(e) =>
                                    handleDefectQuestionChange(
                                      index + 1,
                                      e.target.value
                                    )
                                  }
                                />
                                예
                              </label>
                              <label>
                                <input
                                  type="radio"
                                  name={`defect_q${index + 1}`}
                                  value="no"
                                  onChange={(e) =>
                                    handleDefectQuestionChange(
                                      index + 1,
                                      e.target.value
                                    )
                                  }
                                />
                                아니오
                              </label>
                            </S.RadioGroup>
                          </S.QuestionBox>
                        )
                      )}
                    </S.Section>
                  )}

                  <S.Section>
                    <S.Label>가격</S.Label>
                    <S.Input
                      type="number"
                      name="price"
                      value={formData.price}
                      onChange={handleChange}
                      placeholder="판매 가격을 입력해주세요"
                    />
                  </S.Section>

                  <S.Section>
                    <S.Label>상세 설명</S.Label>
                    <S.TextArea
                      name="description"
                      value={formData.description}
                      onChange={handleChange}
                      placeholder="사용 기간, 제공 구성품, 거래 방식 등을 자세히 설명해주세요"
                    />
                  </S.Section>

                  <S.Section>
                    <S.Label>거래 정보</S.Label>
                    <S.Input
                      type="text"
                      name="sellingInfo.location"
                      value={formData.sellingInfo.location}
                      onChange={handleChange}
                      placeholder="거래 위치를 입력해주세요"
                    />
                    <S.Input
                      type="number"
                      name="sellingInfo.pickupPrice"
                      value={formData.sellingInfo.pickupPrice}
                      onChange={handleChange}
                      placeholder="수거 시 받을 금액을 입력해주세요"
                    />
                  </S.Section>
                </>
              )}

              {formData.tradeType === "구매" && (
                <>
                  <S.Section>
                    <S.Label>구매 정보</S.Label>
                    <S.Input
                      type="text"
                      name="modelName"
                      value={formData.modelName}
                      onChange={handleChange}
                      placeholder="모델명 또는 브랜드를 입력해주세요"
                    />
                    <S.Input
                      type="number"
                      name="buyingInfo.quantity"
                      value={formData.buyingInfo.quantity}
                      onChange={handleChange}
                      min="3"
                      placeholder="수량 (최소 3개)"
                    />
                    <S.Input
                      type="number"
                      name="buyingInfo.desiredPrice"
                      value={formData.buyingInfo.desiredPrice}
                      onChange={handleChange}
                      placeholder="구매 희망 가격을 입력해주세요"
                    />
                    <S.Input
                      type="date"
                      name="buyingInfo.deadline"
                      value={formData.buyingInfo.deadline}
                      onChange={handleChange}
                      placeholder="마감 기한"
                    />
                    <S.TextArea
                      name="buyingInfo.condition"
                      value={formData.buyingInfo.condition}
                      onChange={handleChange}
                      placeholder="제품 조건을 입력해주세요"
                    />
                    <S.TextArea
                      name="description"
                      value={formData.description}
                      onChange={handleChange}
                      placeholder="거래 방식, 위치, 수거 시 추가 가격 등을 자세히 설명해주세요"
                    />
                  </S.Section>
                </>
              )}
            </>
          )}

          <S.SubmitButton type="submit">등록하기</S.SubmitButton>
        </S.Form>
      </S.Container>
      <Navbar />
    </>
  );
};

export default PostCreate;
